mapper.select:
		select
		t.*
		from sys_log t
		where 1=1
		  
           and t.logtitle like concat(concat('%', #{logtitle}),'%')   
        
		  
           and t.content like concat(concat('%', #{content}),'%')   
        
         
        
            and t.createtime >= #{startDate}
        

        
        
            AND t.createtime <= #{endDate}
         
        
        	limit #{start}, #{rows}

select count(*) from sys_log t where 1= 1
and t.logtitle like concat(concat('%', #{logtitle}),'%')
and t.content like concat(concat('%', #{content}),'%')
and t.createtime >= #{startDate}
AND t.createtime <= #{endDate}
